mapper.select:

select 

from RNA_VERIFY


    and USER_ID = #{userId,jdbcType=BIGINT}


    and REAL_NAME = #{realName,jdbcType=VARCHAR}


    and ID_CARD = #{idCard,jdbcType=VARCHAR}


    and APPLY_STATE = #{applyState,jdbcType=TINYINT}


    and APPLY_TIME = #{applyTime,jdbcType=TIMESTAMP}


    and APPLY_TYPE = #{applyType,jdbcType=TINYINT}


    and VERIFYER_ID = #{verifyerId,jdbcType=BIGINT}


    and VERIFY_TIME = #{verifyTime,jdbcType=TIMESTAMP}


    and REJECT_REASON = #{rejectReason,jdbcType=VARCHAR}


    and PIC_ONE = #{picOne,jdbcType=VARCHAR}


    and PIC_TWO = #{picTwo,jdbcType=VARCHAR}


    and PIC_THREE = #{picThree,jdbcType=VARCHAR}


    and PIC_FOUR = #{picFour,jdbcType=VARCHAR}


select count(*) 
from RNA_VERIFY
where ID = #{id,jdbcType=BIGINT}


